export function createEmail(supermarketDiscounts) {
    const formatter = new Intl.NumberFormat('de', {
        style: 'currency',
        currency: 'EUR',
        minimumFractionDigits: 2
    })
    const header = `
        <html lang="de">
            <head>
                <title>Lars Alkohol Radar</title>
                <meta charset="utf-8">
                <style>
                    html, body {
                        margin: 0;
                        padding: 0;
                        font-size: 16px;
                        font-family: Arial, sans-serif;
                        background-color: #eaeaea;
                    }
                    
                    body {
                        display: flex;
                        flex-direction: column;
                        justify-content: space-around;
                        margin: 0 1.5rem;
                    }
                    
                    body > p {
                        margin: 0;
                    }
                    
                    h2 {
                        margin: 0 0 0.5rem 0;
                    }
                    
                    .product-wrapper {
                        display: flex;
                        flex-wrap: wrap;
                        gap: 1rem;
                        margin: 2.5rem 0;
                    }
                    
                    .product {
                        width: 250px;
                        padding: 0.25rem;
                        background-color: #d5d5d5;
                        display: flex;
                        flex-direction: column;
                        word-break: break-all;
                    }
                    
                    .product-image {
                        background-color: white;
                        display: flex;
                        justify-content: center;
                        align-items: center;
                        height: 244px;
                        position: relative;
                        margin: -.25rem -.25rem 0 -.25rem;
                        padding: .25rem;
                    }
                    
                    .product-image img {
                        width: 220px;
                        height: 200px;
                        object-fit: contain;
                    }
                    
                    .product-image .product-price {
                        position: absolute;
                        bottom: 0.25rem;
                        right: 0.25rem;
                        font-size: 1.25rem;
                        font-weight: bold;
                        color: red;
                    }
                    
                    .product p, .product h5 {
                        margin: 0; 
                        color: darkgray;
                    }
                    
                    .product > p {
                        font-size: 0.85rem;
                    }
                </style>
            </head>
            <body>
                <h1>
                    <img
                        src="data:image/png;base64,"
                        alt="Lars Alkohol Radar"
                        draggable="false"
                    >
                </h1>
                
                <hr>
    `;

    let body = '';

    for (const supermarket of supermarketDiscounts) {
        body += `<h2>${supermarket.supermarket} (${supermarket.city || 'bundesweit'})</h2>`;

        if (supermarket.validity) {
            body += `<p>Gültig vom ${new Date(supermarket.validity.from).toLocaleDateString()} bis zum ${new Date(supermarket.validity.until).toLocaleDateString()}</p>`;
        }

        body += '<div class="product-wrapper">';

        for (const product of supermarket.products) {
            body += `
                <div class="product">
                   <div class="product-image">
                    <img src="${product.image}" alt="" draggable="false">
                    <span class="product-price">${formatter.format(product.price)}</span>
                   </div> 
                   <h3>${product.name}</h3>
                   
                   ${product.specialDiscountPrice ? `<h5>Gesonderter Rabatt (z.B. mit Kundenkarte o.ä.): ${formatter.format(product.specialDiscountPrice)} ${product.specialDiscountInPercentage ? `(${product.specialDiscountInPercentage})` : ''}</h5>` : ''}    
                   ${product.oldPrice ? `<p>Alter Preis: ${formatter.format(product.oldPrice)} ${product.discountInPercentage ? `(${product.discountInPercentage})` : ''}</p>` : ''}
                   
                   ${product.description ? `<p>${product.description}</p>` : ''}
                   <!--
                   ${product.baseQuantity ? `<p>${product.baseQuantity}</p>` : ''}
                   ${product.baseUnit ? `<p>${product.baseUnit}</p>` : ''}
                   -->             
                </div>
            `;
        }

        body += '</div>'
    }

    const footer = `
        </tbody>
        </table>
        </body>
        </html>
    `;

    return header + body + footer;
}